// src/Scene.js
import React, { useRef, useEffect, useState } from 'react';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';

const Scene = () => {
  const mountRef = useRef(null);
  const [doorSize, setDoorSize] = useState({ width: 1, height: 2 });

  useEffect(() => {
    const mount = mountRef.current;

    // Создаем сцену
    const scene = new THREE.Scene();

    // Создаем камеру
    const camera = new THREE.PerspectiveCamera(
      75,
      window.innerWidth / window.innerHeight,
      0.1,
      1000
    );
    camera.position.z = 5;

    // Создаем рендерер
    const renderer = new THREE.WebGLRenderer();
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.shadowMap.enabled = true; // Включаем тени для рендерера
    mount.appendChild(renderer.domElement);

    // Создаем контролы для камеры
    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;

    // Создаем источник света
    const light = new THREE.DirectionalLight(0xffffff, 1);
    light.position.set(10, 10, 10);
    light.castShadow = true; // Источник света отбрасывает тени
    scene.add(light);

    // Настройка теней для света
    light.shadow.mapSize.width = 512;  // default
    light.shadow.mapSize.height = 512; // default
    light.shadow.camera.near = 0.5;    // default
    light.shadow.camera.far = 500;     // default

    // Добавляем окружение
    const ambientLight = new THREE.AmbientLight(0x404040); // soft white light
    scene.add(ambientLight);

    // Создаем плоскость (пол) для отображения теней
    const planeGeometry = new THREE.PlaneGeometry(10, 10);
    const planeMaterial = new THREE.MeshStandardMaterial({ color: 0x808080 });
    const plane = new THREE.Mesh(planeGeometry, planeMaterial);
    plane.rotation.x = -Math.PI / 2;
    plane.position.y = -1;
    plane.receiveShadow = true; // Плоскость принимает тени
    scene.add(plane);

    // Добавляем геометрические фигуры
    const geometry1 = new THREE.BoxGeometry(1, 1, 1);
    const material1 = new THREE.MeshStandardMaterial({ color: 0x00ff00, metalness: 0.5, roughness: 0.5 });
    const cube = new THREE.Mesh(geometry1, material1);
    cube.position.set(-2, 0, 0);
    cube.castShadow = true; // Куб отбрасывает тень
    scene.add(cube);

    const geometry2 = new THREE.SphereGeometry(0.5, 32, 32);
    const material2 = new THREE.MeshStandardMaterial({ color: 0x0000ff, metalness: 0.5, roughness: 0.5 });
    const sphere = new THREE.Mesh(geometry2, material2);
    sphere.position.set(2, 0, 0);
    sphere.castShadow = true; // Сфера отбрасывает тень
    scene.add(sphere);

    // Добавляем дверь из дерева
    const textureLoader = new THREE.TextureLoader();
    const doorTexture = textureLoader.load('texture/wood.jpeg');
    const doorGeometry = new THREE.BoxGeometry(doorSize.width, doorSize.height, 0.1);
    const doorMaterial = new THREE.MeshStandardMaterial({ map: doorTexture });
    const door = new THREE.Mesh(doorGeometry, doorMaterial);
    door.position.set(0, doorSize.height / 2 - 1, 0); // Корректировка позиции двери
    door.castShadow = true; // Дверь отбрасывает тень
    door.position.set(0, doorSize.height / 2 - 1, 0); // Корректировка позиции двери
    door.castShadow = true; // Дверь отбрасывает тень
    scene.add(door);

    // Добавляем ручку двери
    const handleGeometry = new THREE.SphereGeometry(0.1, 32, 32);
    const handleMaterial = new THREE.MeshStandardMaterial({ color: 0xCCCCCC,  roughness: 0.2 });
    const handle = new THREE.Mesh(handleGeometry, handleMaterial);
    handle.position.set(doorSize.width / 2 - 0.1, doorSize.height / 2 - 1, 0.15); // Позиция ручки
    handle.castShadow = true; // Ручка отбрасывает тень
    scene.add(handle);

    // Анимационная функция
    const animate = () => {
      requestAnimationFrame(animate);
      cube.rotation.x += 0.01;
      cube.rotation.y += 0.01;
      sphere.rotation.x += 0.01;
      sphere.rotation.y += 0.01;
      controls.update();
      renderer.render(scene, camera);
    };

    animate();

    // Очистка ресурса при размонтировании компонента
    return () => {
      mount.removeChild(renderer.domElement);
    };
  }, [doorSize]);

  const handleResize = (event) => {
    const { name, value } = event.target;
    setDoorSize((prevSize) => ({
      ...prevSize,
      [name]: parseFloat(value),
    }));
  };

  return (
    <div>
      <div ref={mountRef} />
      <div style={{ position: 'absolute', top: 0, left: 0 }}>
        <label>
          Width:
          <input
            type="number"
            name="width"
            value={doorSize.width}
            onChange={handleResize}
          />
        </label>
        <label>
          Height:
          <input
            type="number"
            name="height"
            value={doorSize.height}
            onChange={handleResize}
          />
        </label>
      </div>
    </div>
  );
};

export default Scene;
